import { useState } from "react"
import { EnterButton, StyledChatBox } from "./ChatBox.styled"
import { Axios } from "axios"

export default function ChatBox() {
  const [text, setText] = useState('')

  const autoGrowTextArea = (event) => {
    setText(event.target.value)
    event.target.style.height = 'auto'
    event.target.style.height = `${event.target.scrollHeight}px`
  }  

  const sendMessage = async () => {
    if (text.trim()) {
      const payload = {
        messages: [{ role: "user", content: text }]
      };
      try {
        const response = await axios.post('/api/chat', payload);
        onNewMessage(response.data.choices[0].message.content); // Send response to ChatLog component
        setText(''); // Clear the text area
      } catch (error) {
        console.error('Failed to send message:', error);
      }
    }
  };

  return (
    <StyledChatBox>
      <textarea onInput={autoGrowTextArea} value={text} maxLength="988"></textarea> {/* Add onInput event handler */}
      <EnterButton onClick={sendMessage} disabled={!text.trim()}>{`>>`}</EnterButton>
    </StyledChatBox>
  )
}
