import styled from 'styled-components'

export const StyledChatLog = styled.div`
    /* border: 1px solid red;
    box-sizing: border-box; */
    width: 800px;
    flex-grow: 1;

    .chat-message {
        display: flex;
        align-items: flex-start;
        margin-bottom: 20px;

        .message {
            padding-top: 15px;
        }
    }
`
