import { StyledApp, Nav, ChatBox, ChatLog, EnterButton} from "./App.styled";


function App() {
  const autoGrowTextArea = (event) => {
    // This is the maximum height you want the textarea to grow to
    const maxHeight = 200;

    // Reset height to auto so that scrollHeight calculates correctly
    event.target.style.height = 'auto';
    // Check if the scrollHeight is less than the maximum allowed height
    if (event.target.scrollHeight < maxHeight) {
      event.target.style.height = `${event.target.scrollHeight}px`;
    } else {
      // If the content is taller than the max height, set the height to maxHeight
      // and allow for scrolling
      event.target.style.height = `${maxHeight}px`;
      event.target.style.overflowY = 'auto'; // Enable vertical scrolling
    }
  }

  return (
    <StyledApp className="App">
      <Nav>DungeonGPT</Nav>
