import { StyledApp, Nav, ChatBox, EnterButton} from "./App.styled";
import ChatLog from "../ChatLog/ChatLog";


function App() {
  const autoGrowTextArea = (event) => {
    event.target.style.height = 'auto';
    event.target.style.height = `${event.target.scrollHeight}px`;
  }

  return (
    <StyledApp className="App">
      <Nav>DungeonGPT</Nav>
        {/* <PlayerInfo>Player</PlayerInfo> */}
      <ChatLog />
      <ChatBox>
        <textarea onInput={autoGrowTextArea} maxLength="988"></textarea> {/* Add onInput event handler */}
        <EnterButton>{`>>`}</EnterButton>
      </ChatBox>
    </StyledApp>
  );
}

export default App;
