import styled from 'styled-components'



const centerElements = () => `
    display: flex;
    justify-content: center;
    align-items: center;
`

export const StyledApp = styled.div`
    --active-color: #8BE8D7;
    --inactive-color: #39615A;
    --background-color: #283134;


    width: 100%;
    min-height: 100vh;
    background-color: var(--background-color);
    margin: 0;
    padding: 0;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
    color: #8BE8D7;
`

export const Nav = styled.nav`
    width: 800px;
    height: 50px;
    /* border: 1px solid red;
    box-sizing: border-box; */
    font-weight: 800;
    ${centerElements}
`

export const TextField = styled.div`
  flex-grow: 1; 
  border: 1px solid red;
  height: 100%; // Make it take full height of its parent
  margin: 0 10px; // Give some space between the TextField and the PlayerInfo boxes
  box-sizing: border-box;
`

export const CenterElement = styled.div`
  display: flex; 
  justify-content: space-around; 
  align-items: center; 
  width: 800px; // This should match the width of the Nav and Input components
  flex-grow: 1;
  border: 1px solid red;
  margin: auto; // This centers the CenterElement horizontally within its parent
  position: relative; // Needed for absolute positioning of children
  box-sizing: border-box;
`

export const PlayerInfo = styled.div`
  width: 300px;
  min-height: 300px; // min-height ensures that the content will determine the height, but it won't be smaller than 300px
  border: 1px solid red;
  position: absolute; // Position it absolutely within CenterElement
  top: 50%;
  transform: translateY(-50%); // This will center it vertically
  &:first-child {
    left: 0;
  }
  &:last-child {
    right: 0;
  }
  ${centerElements}
`

export const Input = styled.div`
    width: 800px;
    height: 50px;
    border: 1px solid var(--inactive-color);
    box-sizing: border-box;
    background-color: rgba(0,0,0,0);
    margin-bottom: 20px;
    border-radius: 15px;

    display: flex;
    align-items: center;
    justify-content: right;
`

export const EnterButton = styled.button`
    width: 35px;
    height: 35px;
    background-color: rgba(0,0,0,0);
    border: 1px solid var(--inactive-color);
    border-radius: 12px;
    margin-right: 10px;
    color: var(--background-color);
    background-color: var(--inactive-color);
    font-weight: 800;
`
